mapper.select:
SELECT MEMBER_ID, MEMBER_EMAIL, MEMBER_PW, MEMBER_NAME, MEMBER_DEL, MEMBER_CH, MEMBER_DATE, MEMBER_ADMIN, r FROM (SELECT MEMBER_ID, MEMBER_EMAIL, MEMBER_PW, MEMBER_NAME, MEMBER_DEL, MEMBER_CH, MEMBER_DATE, MEMBER_ADMIN, ROWNUM r FROM (SELECT /*+index(MEMBER IDX_MEMBER_DATE)*/ MEMBER_ID, MEMBER_EMAIL, MEMBER_PW, MEMBER_NAME, MEMBER_DEL, MEMBER_CH, MEMBER_DATE, MEMBER_ADMIN FROM member WHERE member_del = 'Y' AND ${n} like '%'||#{search}||'%' AND MEMBER_DATE <= SYSDATE) WHERE ROWNUM <= #{endrow}) WHERE r >= #{startrow}
AND member_ch = 0
AND member_ch = 1

mapper.update:
UPDATE member SET 

	member_pw = member_pw,


	member_pw = #{member_pw}, 


	member_name = member_name


	member_name = #{member_name}

WHERE member_id = #{member_id}
