mapper.update:
UPDATE MEMBER SET
MEMBER_CH = 1
WHERE MEMBER_EMAIL = #{member_email}

UPDATE MEMBER SET
MEMBER_CH = 0
WHERE MEMBER_ID = #{member_id}

UPDATE MEMBER SET
MEMBER_PW = #{member_pw}
WHERE MEMBER_EMAIL = #{member_email}

UPDATE MEMBER SET
MEMBER_DEL = 'N'
WHERE MEMBER_EMAIL = #{member_email}
